let web3 = window.web3;  

      //set web3 & truffle contract
      if (typeof web3 !== 'undefined') {  
          // Use Mist/MetaMask's provider  

          this.web3 = new Web3(web3.currentProvider);  



          if (web3.eth.coinbase === null) this.isWeb3Locked = true;  

     }else{  
      this.isWeb3 = false;  
