import React, { Component } from 'react';
import config from '../config';

class MenuReview extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: '',
            name:'',
            price:'',
            details:''
        }
    }

    async componentDidMount() {
        const id = await config.methods.id().call();
        const price = await config.methods.price().call();
        await config.methods.GetPackage(this.state.id).call((err,result)=>{
                if(err){
                    console.log(err);
                }
                else{
                    console.log(result);
                    const data =  awatJSON.parse(result);
                    console.log(data._name);
                }
        })
        .then(console.log)
        //.then(this.state.name = _name)
        this.setState({
            id
        });
    }

    render() {
        return (
            <div className="main">
                <div className="ui card package">
                    <div className="content">
                        <div className="header">Package</div>

                        <div className="description">
                            <h3>ID:  {this.state.id} </h3>
                        </div>
                        <div className="description">
                            <h3>Name: {this.state.name}</h3>
                        </div>
                        <div className="description">
                            <h3>Price: {this.state.price}</h3>
                        </div>
                        <div className="description">
                            <h3>Details: </h3>
                        </div>
                    </div>
                    <div className="extra content">
                    </div>
                    <div className="front-deposit">
                        <div className="input input-buy">
                            <div><h4>Buyer recieve to purchase:</h4></div>
                            <div className="ui action input">
                                <input type="text" placeholder="Price" />
                                <button className="ui violet inverted button">Deposit</button>
                            </div>
                        </div>
                        <div className="input input-transport">
                            <div><h4>Shipper recieve to purchase:</h4></div>
                            <div className="ui action input">
                                <input type="text" placeholder="Price" />
                                <button className="ui blue inverted button">Deposit</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default MenuReview;
