import React, { Component } from 'react';
import config2 from '../config2' 
import { Form, Button, Input } from 'semantic-ui-react';
import web3 from '../web3';
class MenuShipperDelivery extends Component {
    constructor(props) {
        super(props);
        this.state ={
            seller:'',
            errorMessage:'',
            name:'',
            price:0,
            id:0,
        }
    }
    async componentDidMount() {
        const accounts = await web3.eth.getAccounts();
        const seller = await config2.methods.seller().call();
        const id = await config2.methods.id().call();
        await config2.methods.GetPackage(id).call({ from: accounts[0] }, (err, result) => {
            if (err) {
                console.log("err: ", err);
            }
            else {
                console.log("result: ", result);
                //var data =  JSON.stringify(result);
                console.log("name: ", result[0]);
                console.log("price: ", result[1]);
                this.setState({
                    name: result[0],
                    price: result[1]
                })
            }
        })
        this.setState({
            seller,
            id
        });
    }
    render() {
        return (
            <div className="main">
                 <div className="info-package">
                    <div className="ui card package">
                    <div className="content">
                    {/* ***** */}
                        <div className="header">Package</div>
                        <div className="description">
                        {/* /***** */}
                            <h3>ID:  <i>{this.state.id}</i> </h3>
                        </div>
                        <div className="description">
                            <h3>Name: <i>{this.state.name}</i></h3>
                        </div>
                        <div className="description">
                            <h3>Price: <i>{this.state.price} Ether</i></h3>
                        </div>

                    </div>
                    <div className="extra content">
                    </div>
                    <div className="front-deposit">
                        <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage} className="input input-buy">
                            <div>
                                <div><h4>Shipper recieve to purchase:</h4></div>
                                <div className="ui action input">
                                    <input value={this.state.id}
                                        onChange={event =>
                                            this.setState({ id: event.target.value })}
                                        type="text" placeholder="Price" />
                                    <button className="ui blue inverted button">Deposit</button>
                                </div>
                            </div>

                        </Form>
                        <div className="ui positive message">
                        <div className="header">Message</div>
                        <p>{this.state.errorMessage}</p>
                        </div>


                    </div>
                </div>
                    </div>
            </div>
        );
    }
}

export default MenuShipperDelivery;
