import React, { Component } from 'react';
import config2 from '../config2' 
import { Form, Button, Input } from 'semantic-ui-react';
import web3 from '../web3';
class MenuShipper extends Component {
    constructor(props) {
        super(props);
        this.state ={
            seller:'',
            errorMessage:'',
            name:'',
            price:0,
            id:0,
        }
    }

    async componentDidMount() {
        const seller = await config2.methods.seller().call();
        const id = await config2.methods.id().call();

        this.setState({
            seller,
            id
        });

    }
    onSubmit = async (event) => {
        event.preventDefault();
        const { name, price, details } = this.state;
        if(name === ''){
            this.setState({
                errorMessage:"Please enter a name!!"
            })
        }
         else if(price === ''){
            this.setState({
                errorMessage:"Please enter price!!"
            })
        }
         else if(details === ''){
            this.setState({
                errorMessage: "Please enter detail a package!!"
            })
        }
        else{
            try {
                const accounts = await  web3.eth.getAccounts();
                // await config2.methods.Ac1_CreatePackage(name, price).send({
                //     from: accounts[0],
                //     gas: '2000000'
                // })
                // await config.methods.Ac2_CreateHash().send({
                //     from: accounts[0]
                // })
                this.setState({
                    errorMessage: 'success!!',       
                });
            }
            catch (err) {
                this.setState({ errorMessage: err.message });
            }
            this.setState({ loading: false });
        }
       this.setState({
           name:'',
           price:'',

       });
    };
    render() {
        return (
            <div className="main">

                    <a className="ui blue image label">Seller
                      <div className="detail">{this.state.seller}</div>
                    </a>
                    <div className="from-creat">
                        <h3>Seller fill out a package information</h3>
                        <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                            <Form.Field>
                                <label>Name</label>
                                <Input
                                    value={this.state.name}
                                    onChange={event =>
                                        this.setState({ name: event.target.value })} 
                                />
                            </Form.Field>

                            <Form.Field>
                                <label>Price in Ether</label>
                                <Input
                                    value={this.state.price}
                                    onChange={event => this.setState({ price: event.target.value })}   
                                />
                            </Form.Field>

                            <Button  primary loading={this.state.loading}>
                                Create!
                            </Button>
                        </Form>

                    </div>

                    <div className="info-package">
                    <div className="ui card package">
                    <div className="content">
                    {/* ***** */}
                        <div className="header">Package</div>
                        <div className="description">
                        {/* /***** */}
                            <h3>ID:  <i>{this.state.id}</i> </h3>
                        </div>
                        <div className="description">
                            <h3>Name: <i>{this.state.name}</i></h3>
                        </div>
                        <div className="description">
                            <h3>Price: <i>{this.state.price}</i></h3>
                        </div>

                    </div>
                    <div className="extra content">
                    </div>
                    <div className="front-deposit">



                        {/* <Form onSubmit={this.onSubmit2} error={!!this.state.errorMessage} className="input input-buy">
                            <div>
                                <div><h4>Shipper recieve to purchase:</h4></div>
                                <div className="ui action input">
                                    <input value={this.state.id}
                                        onChange={event =>
                                            this.setState({ id: event.target.value })}
                                        type="text" placeholder="Price" />
                                    <button className="ui blue inverted button">Deposit</button>
                                </div>
                            </div>

                        </Form> */}


                    </div>
                </div>
                    </div>

                    <div className="ui positive message">
                        <div className="header">Message</div>
                        <p>{this.state.errorMessage}</p>
                        </div>
            </div>
        );
    }
}

export default MenuShipper;
