import React, { Component } from 'react';
import config2 from '../config2' 
import { Form, Button, Input } from 'semantic-ui-react';
import web3 from '../web3';
class MenuShipper extends Component {
    constructor(props) {
        super(props);
        this.state ={
            seller:'',
            errorMessage:'',
            name:'',
            price:0,
            id:0,
        }
    }

    async componentDidMount() {
        const seller = await config2.methods.seller().call();
        this.setState({
            seller,
        });

    }
    onSubmit = async (event) => {
        event.preventDefault();
        const { name, price, details } = this.state;
        if(name === ''){
            this.setState({
                errorMessage:"Please enter a name!!"
            })
        }
         else if(price === ''){
            this.setState({
                errorMessage:"Please enter price!!"
            })
        }
         else if(details === ''){
            this.setState({
                errorMessage: "Please enter detail a package!!"
            })
        }
        else{
            try {
                const accounts = await  web3.eth.getAccounts();
                await config2.methods.Ac1_CreatePackage(name, price).send({
                    from: accounts[0],
                    gas: '2000000'
                })

                this.setState({
                    errorMessage: 'success!!',       
                });
            }
            catch (err) {
                this.setState({ errorMessage: err.message });
            }
            this.setState({ loading: false });
        }
       this.setState({
           name:'',
           price:'',

       });
    };
    render() {
        return (
            <div className="main">

                    <a className="ui blue image label">Seller
                      <div className="detail">{this.state.seller}</div>
                    </a>
                    <div className="from-creat">
                        <h3>Seller fill out a package information</h3>
                        <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                            <Form.Field>
                                <label>Name</label>
                                <Input
                                    value={this.state.name}
                                    onChange={event =>
                                        this.setState({ name: event.target.value })} 
                                />
                            </Form.Field>

                            <Form.Field>
                                <label>Price in Ether</label>
                                <Input
                                    value={this.state.price}
                                    onChange={event => this.setState({ price: event.target.value })}   
                                />
                            </Form.Field>

                            <Button  primary loading={this.state.loading}>
                                Create!
                            </Button>
                        </Form>        
                        <div className="ui positive message">
                        <div className="header">Message</div>
                        <p>{this.state.errorMessage}</p>
                        </div>              
                    </div>

            </div>
        );
    }
}

export default MenuShipper;
