import React, { Component } from "react";
import config2 from "../config2";
import { Form, Button, Input } from "semantic-ui-react";
import web3 from "../web3";
import config from "../config";
class Confirm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hash: "",
      errorMessage: ""
    };
  }

  async componentDidMount() {
    const hash = await config2.methods.hash().call();
  }
  onSubmit = async event => {
    event.preventDefault();
    const { hash } = this.state;
    if (hash === "") {
      this.setState({
        errorMessage: "Please enter hash!!"
      });
    } else {
      try {
        const accounts = await web3.eth.getAccounts();
        await config.methods.ConfirmOfBuyer(this.state.hash).send({
          from: accounts[1]
        });
        // await config2.methods.ConFirmOfSeller(hash).call({from: accounts[0]});
        this.setState({
          errorMessage: "success!!"
        });
      } catch (err) {
        this.setState({ errorMessage: err.message });
      }
      this.setState({ loading: false });
    }
    this.setState({
      hash: ""
    });
  };
  onSubmit2 = async event => {
    event.preventDefault();
    const { hash } = this.state;
    if (hash === "") {
      this.setState({
        errorMessage: "Please enter hash!!"
      });
    } else {
      try {
        const accounts = await web3.eth.getAccounts();
        await config2.methods.ConFirmOfSeller(this.state.hash).send({
          from: accounts[0]
        });
        // await config2.methods.ConFirmOfSeller(hash).call({from: accounts[0]});
        this.setState({
          errorMessage: "success!!"
        });
      } catch (err) {
        this.setState({ errorMessage: err.message });
      }
      this.setState({ loading: false });
    }
    this.setState({
      hash: ""
    });
  };
  render() {
    return (
      <Router>
      <Switch>
        <div>

          <Route path="/" exact component={MenuHome} />
          <Route path="/review" component={MenuReview} />
          <Route path="/shipper" component={MenuShipper} />
          <Route path="/shipperdilivery" component={MenuShipperDelivery} />
          <Route path="/confirm" component={Confirm} />
          <div>
            <Footer />
          </div>
        </div>
      </Switch>
    </Router>
    );
  }
}

export default Confirm;
