import React, { Component } from 'react';
import config from '../config';
import web3 from '../web3';
import { Form } from 'semantic-ui-react';
class MenuReview extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: '',
            name: '',
            price: '',
            details: '',
            details: '',
           balanceBuyer:0

        }
    }

    async componentDidMount() {

        const balanceBuyer = web3.eth.getBalance(accounts[1]);
        const accounts = await web3.eth.getAccounts();
        const id = await config.methods.id().call();
        const price = await config.methods.price().call();
        //const name = await config.methods.GetName(id).call();
        await config.methods.GetPackage(id).call({ from: accounts[0] }, (err, result) => {
            if (err) {
                console.log("err: ", err);
            }
            else {
                console.log("result: ", result);
                //var data =  JSON.stringify(result);
                console.log("name: ", result[0]);
                console.log("details: ", result[2]);
                this.setState({
                    name: result[0],
                    details: result[2]
                })
            }
        })
        // await config.methods.GetPackage(this.state.id).call({from : accounts[0]})
        // .then(console.log);
        this.setState({
            id,
            price,

        });
    }
    onSubmit = async (event) => {
        event.preventDefault();
        try {
            const id = await config.methods.id().call();
            const accounts = await web3.eth.getAccounts();
            await config.methods.Ac3_ApplyBuy(id).send({
                from: accounts[1],
                value: web3.utils.toWei(this.state.price, 'ether')
            })
            this.setState({ errorMessage: "Success!!" });
        }
        catch (err) {
            this.setState({ errorMessage: err.message });
        }

    }
    render() {
        return (
            <div className="main">
            <h1>{this.state.balanceBuyer}</h1>
                <div className="ui card package">
                    <div className="content">
                        <div className="header">Package</div>

                        <div className="description">
                            <h3>ID:  <i>{this.state.id}</i> </h3>
                        </div>
                        <div className="description">
                            <h3>Name: <i>{this.state.name}</i></h3>
                        </div>
                        <div className="description">
                            <h3>Price: <i>{this.state.price}</i></h3>
                        </div>
                        <div className="description">
                            <h3>Details: <i>{this.state.details} </i></h3>
                        </div>
                    </div>
                    <div className="extra content">
                    </div>
                    <div className="front-deposit">
                        <di>
                            <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage} className="input input-buy">
                                <div><h4>Buyer recieve to purchase:</h4></div>
                                <div className="ui action input">
                                    <input value={this.state.id}
                                        onChange={event =>
                                            this.setState({ id: event.target.value })}
                                        type="text" placeholder="Price" />
                                    <button  className="ui violet inverted button">Deposit</button>
                                </div>
                            </Form>
                        </di>


                        <from>
                            <div className="input input-transport">
                                <div><h4>Shipper recieve to purchase:</h4></div>
                                <div className="ui action input">
                                    <input
                                        value={this.state.id}
                                        onChange={event =>
                                            this.setState({ id: event.target.value })}
                                        type="text" placeholder="Price" />
                                    <button loading={this.state.loading} className="ui blue inverted button">Deposit</button>
                                </div>
                            </div>
                        </from>

                    </div>
                </div>
            </div>
        );
    }
}

export default MenuReview;
